(function($){var def={speed:500,easing:''};$.fn.vkSlidedoor=function(cfg){var ops={wrapper:'.slidedoor-wrap',dl:'dl',trigger:'dt',autoplay:false,direction:'ltr',looptimeout:3000,dtwidth:29,ddpadleft:1,ddwidth:392};$.extend(ops,cfg);$.extend(def,cfg);return this.each(function(){var $this=$(this),$dls=$this.find(ops.dl),$dts=$this.find(ops.trigger),$ele=$this.find(ops.wrapper);var i=0;$dls.each(function(){$(this).css("left",i+"px");i+=ops.dtwidth;});var overwidth=$dls.length*ops.dtwidth+ops.ddwidth-ops.ddpadleft,mainwidth=overwidth+ops.ddwidth+10;$this.width(overwidth);$ele.width(mainwidth);$this.find(ops.dl+'.current').nextAll(ops.dl).elemsMove(ops.ddwidth);if(ops.autoplay){var timeout=setInterval(function(){$ele.activeDoors(ops);},ops.looptimeout);}$dts.click(function(){if(ops.autoplay){clearInterval(timeout);}var indexthis=$dts.index(this);$ele.activeDoors(ops,indexthis);});});};$.fn.activeDoors=function(ops,indexthis){var $curr=this.find('.current').removeClass('current'),indexcurr=$curr.index(),$dls=this.find(ops.dl),dlslen=$dls.length;if(arguments.length==2){$dls.each(function(index){if(index<=indexthis&&index>indexcurr){$(this).elemsMove(-ops.ddwidth);}else if(index<=indexcurr&&index>indexthis){$(this).elemsMove(ops.ddwidth);}if(index==indexthis){$(this).addClass('current');}});}else if(ops.direction=='ltr'){if(indexcurr==(dlslen-1)){this.find(ops.dl+':first-child').addClass('current').nextAll(ops.dl).elemsMove(ops.ddwidth);}else{$curr.next().elemsMove(-ops.ddwidth).addClass('current');}}else if(ops.direction=='rtl'){if(indexcurr==0){$curr.nextAll(ops.dl).elemsMove(-ops.ddwidth);this.find(ops.dl+':last-child').addClass('current');}else{$curr.elemsMove(ops.ddwidth);$curr.prev().addClass('current');}}};$.fn.elemsMove=function(val){return this.each(function(){var leftcurr=$(this).css('left'),newcurr=parseInt(leftcurr)+val;$(this).stop(true,true).animate({left:newcurr},def.speed,def.easing);});};})(jQuery);